// \u6240\u6709 mixins \u90fd\u9700\u8981:\ntype Constructor<T = NonNullable<unknown>> = new (...args: any[]) => T\n\n/////////////\n// mixins \u4f8b\u5b50\n////////////\n\n// \u6dfb\u52a0\u5c5e\u6027\u7684\u6df7\u5408\u4f8b\u5b50\nfunction TimesTamped<TBase extends Constructor>(Base: TBase) {\n  return class extends Base {\n    timestamp = Date.now()\n  }\n}\n\n// \u6dfb\u52a0\u5c5e\u6027\u548c\u65b9\u6cd5\u7684\u6df7\u5408\u4f8b\u5b50\nfunction ActiveTable<TBase extends Constructor>(Base: TBase) {\n  return class extends Base {\n    isActivated = false\n\n    activate() {\n      this.isActivated = true\n    }\n\n    deactivate() {\n      this.isActivated = false\n    }\n  }\n}\n\n///////////\n// \u7ec4\u5408\u7c7b\n///////////\n\n// \u7b80\u7b54\u7684\u7c7b\nclass User {\n  name = ''\n}\n\n// \u6dfb\u52a0 TimesTamped \u7684 User\nconst TimestampedUser = TimesTamped(User)\n\n// Tina TimesTamped \u548c ActiveTable \u7684\u7c7b\nconst TimestampedActiveTableUser = TimesTamped(ActiveTable(User))\n\n//////////\n// \u4f7f\u7528\u7ec4\u5408\u7c7b\n//////////\n\nconst timestampedUserExample = new TimestampedUser()\nconsole.log(timestampedUserExample.timestamp)\n\nconst timestampedActiveTableUserExample = new TimestampedActiveTableUser()\nconsole.log(timestampedActiveTableUserExample.timestamp)\nconsole.log(timestampedActiveTableUserExample.isActivated)\n"})})]})}function p(n={}){const{wrapper:e}={...(0,a.R)(),...n.components};return e?(0,i.jsx)(e,{...n,children:(0,i.jsx)(c,{...n})}):c(n)}},86145:(n,e,t)=>{t.d(e,{R:()=>o,x:()=>r});var s=t(57140);const i={},a=s.createContext(i);function o(n){const e=s.useContext(a);return s.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function r(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(i):n.components||i:o(n.components),s.createElement(a.Provider,{value:e},n.children)}}}]);